import React from 'react';
import './Order.css';
import moment from "moment";
import CheckoutProducts from './CheckoutProducts';

function Order({order}) {
    return (
        <div className="order">
            <h2>Order</h2>
            <p>{moment.unix(order.data.created).format("MMM DD YYYY , hh:mm")}</p>

            <p className="order_id">
                <small>{order.id}</small>
            </p>
            {order.data.basket?.map(item =>(
                <CheckoutProducts
                             id={item.id}
                            title={item.title}
                            image={item.image}
                            price={item.price}
                            rating={item.rating}
                        />
            ))}
            <CurrencyFormat
                    renderText = {(value) => (
                    <h3>Order Total: {value}</h3>
                )}
                decimalScale={2}
                value={order.data.amount / 100}
                displayType={"text"}
                thousandSeparator={true}
                prefix={"RM"}
                />
        </div>
    )
}

export default Order
